import React, { Component } from 'react';

// Components
import Button from '../Button';
import Modal from '../Modal';

// Icons
import { MdCloudDownload, MdKeyboardArrowDown } from 'react-icons/md';

// Style
import { Container, LoginText, Country } from './styles';

class Login extends Component {
	state = {
		showModal: false,
	};

	openModal = () => {
		this.setState({ showModal: !this.state.showModal });
	};

	closeModal = e => {
		const y = e.screenY;
		const x = e.screenX;
		console.log(x);
		if (y > 763 || y < 347) {
			this.setState({ showModal: !this.state.showModal });
		}
	};

	countryDropDown() {
		alert('Countries will be coming soon');
	}

	installButton() {
		alert('Ark Software will be coming soon');
	}
	render() {
		return (
			<Container>
				<span>
					<LoginText>
						<a onClick={() => this.openModal()}>Login</a>
						&nbsp; or &nbsp;
						<a onClick={() => this.openModal()}>Create Account</a>
					</LoginText>
					<Country onClick={() => this.countryDropDown()}>
						Country <MdKeyboardArrowDown size={15} />
					</Country>
					<Button
						type="ark"
						label="Install Ark"
						icon={<MdCloudDownload />}
						onClick={() => this.installButton()}
					/>
				</span>
				{this.state.showModal && (
					<span onClick={e => this.closeModal(e)}>
						<Modal />
					</span>
				)}
			</Container>
		);
	}
}

export default Login;
