import styled from 'styled-components';

const Container = styled.div``;

const BackDrop = styled.div`
	position: absolute;
	top: 0;
	bottom: 0;
	width: 100%;
	z-index: 1;
	background-color: rgba(0, 0, 0, 0.4);
`;

const Content = styled.div`
	background-color: #242424;
	border: 1px solid rgba(0, 166, 255, 0.4);
	border-radius: 5px;
	width: 60rem;
	height: 40rem;
	position: absolute;
	left: 50%;
	top: 50%;
	transform: translate(-50%, -50%);
	z-index: 2;
	display: grid;
	grid-template-columns: 1fr 1fr;
	grid-template-rows: 1fr;
	justify-items: center;
	align-items: center;
`;

const LoginCreate = styled.div`
	display: flex;
`;

const Login = styled.div`
	width: 50%;
	height: 230px;
	padding: 5px;

	h3:first-child {
		margin-bottom: 3rem;
	}
	img {
		height: 5rem;
		width: 5rem;
	}

	input {
		margin: 0.5rem 0 1rem 0;
	}

	button {
		margin-left: 18rem;
	}
`;

const LogoStyle = styled.span`
	display: flex;
	align-items: center;
	justify-content: center;
	margin-top: 2rem;

	> img {
		height: 7rem;
		width: 7rem;
	}

	h1 {
		font-size: 4rem;
	}
`;

const Create = styled.div`
	width: 100%;
	height: 230px;
	padding: 5px;

	button {
		padding: 1.2rem 2.5rem;
		font-weight: 600;
		margin: 2rem;
		font-size: 1.8rem;
	}

	p {
		margin-bottom: 3rem;
		color: var(--medium-text-color);
	}
`;

export { Container, Content, Login, Create, BackDrop, LogoStyle, LoginCreate };
