import styled from 'styled-components';

const Container = styled.span`
	grid-column: 2 / span 3;
	justify-self: end;
	margin: 0.5rem;
	> a {
		color: var(--blue);
		text-decoration: none;
		:hover {
			color: var(--light-text-color);
		}
	}

	> span {
		display: flex;
		align-items: center;
		background: #353535;
		padding-left: 1rem;
	}

	> p {
		margin: 0.5rem;
	}
`;

export { Container };
