import './Modal.css';

class Modal extends Component {
	render() {
		return (
			<div className="modal">
				<div className="error-message">Error</div>
				<div className="modal-content" ref={this.setWrapperRef}>
					<h3>
						Login or Create Account
						<button>X</button>
					</h3>
					<div className="login-create">
						<div className="login">
							<h4>Login</h4>
							<form>
								<div className="wrapper">
									<div className="input-group">
										<input type="email" id="login-email" placeholder="Email" />
									</div>
									<div className="input-group">
										<input type="password" id="login-password" placeholder="Password" />
									</div>
									<div className="input-group">
										<button type="submit" value="submit" id="login-submit">
											Submit
										</button>
									</div>
								</div>
							</form>
						</div>
						<div className="create">
							<h4>Create</h4>
							<p>
								Creating an Ark account is easy and free. You can choose whether to download our
								software or use the website only. Please note that only by downloading Ark software you
								will be able to download movies to your computer.
							</p>
							<button type="button" className="join-ark">
								Join Ark
							</button>
						</div>
						<div className="hidden-form">
							<form>
								<div className="wrapper">
									<div className="input-group">
										<input
											type="text"
											id="firstname"
											ref={firstname => (this.firstname = firstname)}
											placeholder="First Name"
										/>
									</div>
									<div className="input-group">
										<input
											type="text"
											id="lastname"
											ref={lastname => (this.lastname = lastname)}
											placeholder="Last Name"
										/>
									</div>
									<div className="input-group">
										<input
											type="email"
											id="email"
											ref={email => (this.email = email)}
											placeholder="Email"
										/>
									</div>
									<div className="input-group">
										<input
											type="password"
											id="password"
											ref={password => (this.password = password)}
											placeholder="Password"
										/>
									</div>
									<div className="input-group">
										<button type="submit" value="submit" id="create-submit">
											Submit
										</button>
									</div>
								</div>
							</form>
						</div>
					</div>
				</div>
			</div>
		);
	}
}

export default Modal;
