import styled from 'styled-components';

const Container = styled.div`
	margin: 1rem;
	img {
		height: 25rem;
	}
`;
const Header = styled.div`
	display: flex;
	align-items: center;
`;
const Star = styled.span`
	img {
		position: relative;
		span {
			position: aboslute;
		}
	}
`;
const Info = styled.div``;
const Cast = styled.div``;

export { Container, Star, Header, Info, Cast };
