import React, { Component } from 'react';

import { Container, Banner } from './styles';

const IMAGES = ['/banners/gang_squad.jpg', '/banners/harry_potter.jpg', '/banners/life_death.jpg'];

import { MdKeyboardArrowLeft, MdKeyboardArrowRight } from 'react-icons/md';

class Banners extends Component {
	state = {
		img: IMAGES[0],
		index: 0,
	};

	handleBannerClick() {
		const nextIndex = index + 1;
		this.setState({ img: nextIndex });
	}

	renderBanners() {
		const { img } = this.state;
		return (
			<Banner>
				<MdKeyboardArrowLeft size={45} onClick={() => this.handleBannerClick()} />
				<img src={img} />
				<MdKeyboardArrowRight size={45} onClick={() => this.handleBannerClick()} />
			</Banner>
		);
	}

	render() {
		return <Container>{this.renderBanners()}</Container>;
	}
}

export default Banners;
