import styled from 'styled-components';

const Container = styled.div`
	a {
		font-size: 2.4rem;
		color: #b3b3b3;
		text-decoration: none;
		margin-left: 2rem;

		:hover {
			text-shadow: 0px 0px 16px white;
			color: white;
		}
	}
`;

export { Container };
