import styled from 'styled-components';
import { breakPoints } from '../../global/breakpoints';
const Container = styled.div``;

const Banner = styled.div`
	margin: 0.5rem 0;
	width: 100%;
	position: relative;
	display: flex;
	align-items: center;
	img {
		width: 100%;
		box-shadow: 0 0 10px black;

		:hover {
			cursor: pointer;
		}
	}

	svg {
		:nth-child(1) {
			background: rgba(0, 0, 0, 0.4);
			height: 90%;
			position: absolute;
			:hover {
				background: rgba(0, 0, 0, 0.8);
				cursor: pointer;
				color: var(--blue);
			}
		}
		:nth-child(3) {
			position: absolute;
			right: 0;
			background: rgba(0, 0, 0, 0.4);
			height: 90%;
			:hover {
				background: rgba(0, 0, 0, 0.8);
				cursor: pointer;
				color: var(--blue);
			}
		}
	}
`;

export { Container, Banner };
