import styled, { css } from 'styled-components';

const Container = styled.div`
	display: flex;
	flex: 1;
	flex-direction: column;
	${props => {
		if (props === 1) {
			return css`
				var(--wrap-1)
			`;
		} else if (props === 2) {
			return css`
				var(--wrap-2)
			`;
		}
		if (props === 3) {
			return css`
				var(--wrap-3)
			`;
		}
		if (props === 4) {
			return css`
				var(--wrap-4)
			`;
		}
		if (props === 5) {
			return css`
				var(--wrap-5)
			`;
		}
	}}
`;
const MovieItem = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;

	img {
		height: 25rem;

		:hover {
			cursor: pointer;
		}
	}
`;
const Title = styled.div`
	display: flex;
	align-items: center;
	justify-content: center;
	overflow-wrap: break-word;
	word-wrap: break-word;
	hyphens: auto;
`;
const Price = styled.div`
	display: flex;
	align-items: center;
	justify-content: center;
`;

export { Container, MovieItem, Title, Price };
