import styled from 'styled-components';

const Container = styled.div`
	display: flex;
	margin: 3.5rem;
	img {
		height: 25rem;
	}

	p {
		margin: 1rem;
		width: 35rem;
	}
`;
const Header = styled.div`
	display: flex;
	align-items: center;
	margin: -2.2rem 1rem 0 1rem;

	h1 {
		margin-right: 1rem;
	}

	span {
		font-size: 1.5rem;
		margin-right: 1rem;
	}
`;
const Star = styled.div`
	position: relative;
	svg {
		color: #0190de;
	}
	span {
		position: absolute;
		top: 22px;
		left: 21px;
		z-index: 1;
		font-size: 1.5rem;
		font-weight: bold;
	}
`;
const Links = styled.div`
	margin-left: auto;
	background: linear-gradient(to left, #282828 90%, var(--darker-grey));
	padding: 0.3rem 0.3rem 0.3rem 2rem;
	border-radius: 1rem;
	a {
		text-decoration: none;
		color: var(--blue);
		font-size: 1.6rem;
		font-weight: bold;
		margin-right: 0.6rem;
	}
`;
const Info = styled.div`
	margin-left: 1rem;
	span {
		margin-right: 0.5rem;
	}
`;
const Cast = styled.div`
	display: flex;
	flex-direction: column;
	margin: 1rem;
`;
const Details = styled.div`
	width: 100%;
`;

export { Container, Star, Header, Info, Cast, Details, Links };
