import React, { Component } from 'react';

// Components
import Title from '../Title';

// Styles
import { Container, Star, Header } from './styles';
import { MdStar } from 'react-icons/md';

const IMAGES = ['/movies/harry_potter.jpg'];

class Hero extends Component {
	state = {
		img: IMAGES[0],
	};
	renderImage() {
		return (
			<>
				<img src={this.state.img} />
			</>
		);
	}

	renderDetails() {
		return (
			<>
				<Header>
					<Title type="h1" title="Harry Potter" />
					<span>1953</span>
					<Star>
						<MdStar size={50} color="var(--blue)" />
						9.5
					</Star>
				</Header>
				<p>
					A jaded nightclub owner in war-torn Casablanca, whose loyalties are put to the test when his old
					flame, Ingrid Bergman, reappears to seek Rick's help in escaping from the Nazis.
				</p>
			</>
		);
	}

	render() {
		return (
			<Container>
				{this.renderImage()}
				{this.renderDetails()}
			</Container>
		);
	}
}

export default Hero;
