import styled from 'styled-components';

const Container = styled.span`
	grid-column: 2 / span 3;
	justify-self: end;
	margin: 0.5rem;
	background: #353535;
	height: 3rem;
	width: 50rem;
	a {
		color: var(--blue);
		text-decoration: none;
		:hover {
			color: var(--light-text-color);
		}
	}

	span {
		display: flex;
		align-items: center;
	}
`;

export { Container };
