import styled from 'styled-components';

const Container = styled.div`
	position: relative;

	svg {
		position: absolute;
		:hover {
			cursor: pointer;
			color: var(--blue);
		}
		:nth-child(1) {
			bottom: 50%;
		}
		:nth-child(3) {
			bottom: 50%;
			right: 0;
		}
	}
`;

const Banner = styled.div`
	margin: 0.5rem 0;
	display: flex;
	align-items: center;
	justify-content: center;
	width: 100%;
	img {
		width: 275px;
		height: 200px;
		box-shadow: 0 0 10px black;

		:hover {
			cursor: pointer;
		}
	}
`;

export { Container, Banner };
