import { css } from 'styled-components';
/*
phone = 600px
tab-port = 900px
tab-land = 1200px
desktop = 1800px
1em = 16px (browser font-size)
*/

const media = breakpoint => {
	switch (breakpoint) {
		case 'phone':
			return css`
				@media (max-width: 37.5em) {
				} /* 600px */
			`;
	}
};

export default media;
