import React, { Component } from 'react';

// Components
import Title from '../Title';

// Styles
import { Container, Star } from './styles';
import { MdStar } from 'react-icons/md';

const IMAGES = ['/movies/harry_potter.jpg'];

class Hero extends Component {
	state = {
		img: IMAGES[0],
	};
	renderImage() {
		return (
			<>
				<img src={this.state.img} />
			</>
		);
	}

	renderDetails() {
		return (
			<>
				<Title type="h1" title="Harry Potter" />
				<span>1953</span>
				<Star>
					<MdStar size={50} color="var(--blue)" />
					9.5
				</Star>
			</>
		);
	}

	render() {
		return (
			<Container>
				{this.renderImage()}
				{this.renderDetails()}
			</Container>
		);
	}
}

export default Hero;
