import React, { Component } from 'react';
import Link from 'next/link';

class Genre extends Component {
	state = {
		genre: [
			{ id: 1, name: 'Fantasy' },
			{ id: 2, name: 'Action' },
			{ id: 3, name: 'Horror' },
		],
	};

	render() {
		const { genre } = this.state;

		return (
			<>
				{genre.map(item => {
					<Link href={`/genre/${item.name}?id=${item.id}`}>
						<>
							<span>{item.name}</span>
						</>
					</Link>;
				})}
			</>
		);
	}
}

export default Genre;
