import React, { Component } from 'react';
import Link from 'next/link';

// Style
import { Container, Content, BackDrop } from './styles';

class MobileModal extends Component {
	state = {
		openMobileModal: false,
	};

	handleMobileModal() {
		this.setState({ openMobileModal: !this.state.openMobileModal });
	}

	render() {
		return (
			<Container>
				<BackDrop onClick={() => this.props.handleMobileModal()} />
				<Content>
					<Link href="/">
						<a>Login</a>
					</Link>
					<Link href="/">
						<a>Store</a>
					</Link>
					<Link href="/">
						<a>Library</a>
					</Link>
					<Link href="/">
						<a>Community</a>
					</Link>
					<Link href="/">
						<a>Support</a>
					</Link>
				</Content>
			</Container>
		);
	}
}

export default MobileModal;
