import React, { Component } from 'react';

// Components
import Button from '../Button';
import Input from '../Input';
import Logo from '../Logo';
import Title from '../Title';

// Icons
import { MdCloudDownload, MdKeyboardArrowDown } from 'react-icons/md';

// Style
import { Container, Content, BackDrop, Login, Create, LogoStyle, Header } from './styles';

class Modal extends Component {
	render() {
		return (
			<Container>
				<BackDrop onClick={() => this.props.handleModal()} />
				<Content>
					<Header>
						<Title type="h3" title="Sign In" />
						<Title type="h3" title="Create Account" />
					</Header>
					<Login>
						<Title type="h3" title="Sign In" />
						<Title type="h3" title="Ark Existing Account" />
						<Input />
						<Title type="h3" title="Password" />
						<Input />
						<Button type="ark" label="Sign in" />
						<LogoStyle>
							<Logo />
							<h1>Ark</h1>
						</LogoStyle>
					</Login>
					<Create>
						<Title type="h3" title="Create Account" />
						<p>
							Creating an Ark account is easy and free. You can choose whether to download our software or
							use the website only. Please note that only by downloading Ark software you will be able to
							download movies to your computer.
						</p>
						<Button type="ark" label="JOIN ARK" />
					</Create>
				</Content>
			</Container>
		);
	}
}

export default Modal;
