import React, { Component } from 'react';
import Link from 'next/link';

import { Container, Banner } from './styles';

import { MOVIES } from '../../utilities/movies';

import { MdKeyboardArrowLeft, MdKeyboardArrowRight } from 'react-icons/md';

class Banners extends Component {
	state = {
		index1: 0,
		index2: 1,
		index3: 2,
	};

	handleBannerClick() {
		const { index } = this.state;

		if (index < MOVIES.length - 1) {
			this.setState({ index: index + 1 });
		} else {
			this.setState({ index: 0 });
		}
	}

	render() {
		const { index1, index2, index3 } = this.state;
		return (
			<Container>
				<MdKeyboardArrowLeft size={45} onClick={() => this.handleBannerClick()} />
				<Banner>
					<Link href={`/movie/${MOVIES[index1].title}?movie=${MOVIES[index1].id}`}>
						<a>
							<img src={MOVIES[index].banner} />
						</a>
					</Link>
					<Link href={`/movie/${MOVIES[index2].title}?movie=${MOVIES[index2].id}`}>
						<a>
							<img src={MOVIES[index].banner} />
						</a>
					</Link>
					<Link href={`/movie/${MOVIES[index3].title}?movie=${MOVIES[index3].id}`}>
						<a>
							<img src={MOVIES[index].banner} />
						</a>
					</Link>
				</Banner>
				<MdKeyboardArrowRight size={45} onClick={() => this.handleBannerClick()} />
			</Container>
		);
	}
}

export default Banners;
