import React, { Component } from 'react';

import { Container } from './styles';

const IMAGES = ['/movies/harry_potter.jpg'];
const MOVIE = {};

class Hero extends Component {
	state = {
		img: IMAGES[0],
	};
	renderImage() {
		return (
			<>
				<img src={this.state.img} />
			</>
		);
	}

	renderDetails() {}

	render() {
		return (
			<Container>
				{this.renderImage()} {this.renderDetails()}
			</Container>
		);
	}
}

export default Hero;
