import React, { Component } from 'react';

// Components
import Button from '../Button';
import Modal from '../Modal';

// Icons
import { MdCloudDownload, MdKeyboardArrowDown } from 'react-icons/md';

// Style
import { Container, LoginText, Country } from './styles';

class Login extends Component {
	state = {
		showModal: false,
	};

	openModal = () => {
		this.setState({ showModal: !this.state.showModal });
	};

	closeModal = e => {
		console.log(e.screenX);
	};

	onCountryClick() {
		alert('Countries will be coming soon');
	}

	onButtonClick() {
		alert('Ark Software will be coming soon');
	}
	render() {
		return (
			<Container>
				<span>
					<LoginText>
						<a onClick={() => this.openModal()}>Login</a>
						&nbsp; or &nbsp;
						<a onClick={() => this.openModal()}>Create Account</a>
					</LoginText>
					<Country onClick={() => this.onCountryClick()}>
						Country <MdKeyboardArrowDown size={15} />
					</Country>
					<Button
						type="ark"
						label="Install Ark"
						icon={<MdCloudDownload />}
						onClick={() => this.onButtonClick()}
					/>
				</span>
				{this.state.showModal && (
					<span onClick={e => this.closeModal(e)}>
						<Modal />
					</span>
				)}
			</Container>
		);
	}
}

export default Login;
