import React, { Component } from 'react';

import { Container, Banner } from './styles';

const IMAGES = ['/banners/harry_potter.jpg', '/banners/avengers.jpg', '/banners/life_and_death.jpg'];

class Banners extends Component {
	state = {
		img: IMAGES[0],
		index: 0,
	};

	onChange = index => {
		let option = index < 0 ? IMAGES.length - 1 : index >= IMAGES.length ? 0 : index;
		this.setState({ img: IMAGES[option], index: option });
	};

	componentDidMount() {
		this.scroll = window.setInterval(() => this.onChange(this.state.index + 1), 3000);
	}

	componentWillUnmount() {
		window.clearInterval(this.scroll);
	}

	renderBanners() {
		const { img } = this.state;
		return (
			<Banner>
				<img src={img} width="100%" height="300rem" />
			</Banner>
		);
	}

	render() {
		return <Container>{this.renderBanners()}</Container>;
	}
}

export default Banners;
