import styled from 'styled-components';

const Container = styled.div``;

const Banner = styled.div`
	margin: 0.5rem 0;
	width: 100%;
	display: flex;
	align-items: center;
	justify-content: center;
	img {
		width: 100%;
		box-shadow: 0 0 10px black;

		:hover {
			cursor: pointer;
		}
	}

	svg {
		:hover {
			cursor: pointer;
			color: var(--blue);
		}
		:nth-child(1) {
			bottom: 50%;
		}
		:nth-child(3) {
			bottom: 50%;
			right: 0;
		}
	}
`;

export { Container, Banner };
