import styled from 'styled-components';

const Container = styled.div``;

const Banner = styled.div`
	margin: 0.5rem 0;
	position: relative;
	img {
		width: 100%;
		box-shadow: 0 0 10px black;
	}

	svg {
		position: absolute;
		:nth-child(1) {
			bottom: 50%;
		}
		:nth-child(2) {
			bottom: 50%;
			left: -50%;
		}
	}
`;

export { Container, Banner };
